import React, { useRef } from "react";
import { motion, useScroll, useTransform } from "framer-motion";

const images = [
  { id: 1, src: "https://flowbite.com/docs/images/carousel/carousel-1.svg" },
  { id: 2, src: "https://flowbite.com/docs/images/carousel/carousel-2.svg" },
  { id: 3, src: "https://flowbite.com/docs/images/carousel/carousel-3.svg" },
];

function useParallax(scrollYProgress: any, distance: number) {
  return useTransform(scrollYProgress, [0, 1], [-distance, distance]);
}

const ParallaxSlide = ({ image, scrollYProgress }: { image: any; scrollYProgress: any }) => {
  const y = useParallax(scrollYProgress, 50);

  return (
    <motion.div
      className="relative w-screen h-screen flex items-center justify-center snap-center"
      style={{ scrollSnapAlign: "center" }}
    >
      <img
        src={image.src}
        alt={`Image ${image.id}`}
        className="absolute w-full h-full object-cover"
      />
      {/* Parallax effect for the image title */}
      <motion.h2
        className="absolute bottom-10 left-10 text-white text-4xl font-bold"
        style={{ y }}
      >
        Image #{image.id}
      </motion.h2>
    </motion.div>
  );
};

const HomeCarousel = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ target: ref });

  return (
    <div
      ref={ref}
      className="relative w-screen h-screen overflow-y-scroll snap-y snap-mandatory"
      style={{ scrollBehavior: "smooth" }}
    >
      {images.map((image) => (
        <ParallaxSlide key={image.id} image={image} scrollYProgress={scrollYProgress} />
      ))}
    </div>
  );
};

export default HomeCarousel;
