import React, { useRef } from "react";
import { motion, useScroll } from "framer-motion";

const slides = [
  {
    id: 1,
    title: "Latest Collection",
    description: "Explore our handpicked collection for the season.",
    image: "https://via.placeholder.com/1200x800?text=Latest+Collection",
    link: "/latest-collection",
  },
  {
    id: 2,
    title: "New Arrivals",
    description: "Be the first to grab our latest arrivals.",
    image: "https://via.placeholder.com/1200x800?text=New+Arrivals",
    link: "/new-arrivals",
  },
  {
    id: 3,
    title: "Best Sellers",
    description: "Shop the most-loved products by our customers.",
    image: "https://via.placeholder.com/1200x800?text=Best+Sellers",
    link: "/best-sellers",
  },
];

const HomeCarousel = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ target: ref });

  return (
    <div
      ref={ref}
      className="relative w-screen h-screen overflow-y-scroll snap-y snap-mandatory"
    >
      {slides.map((slide) => (
        <motion.div
          key={slide.id}
          className="relative w-full h-screen flex items-center justify-center snap-center"
        >
          {/* Background Image with Overlay */}
          <div className="absolute inset-0">
            <img
              src={slide.image}
              alt={slide.title}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-yellow-600 via-transparent to-yellow-800 opacity-80"></div>
          </div>

          {/* Overlay Content */}
          <div className="relative z-10 text-center text-white px-6">
            <h1 className="text-5xl font-extrabold tracking-tight drop-shadow-[0_4px_4px_rgba(245,158,11,0.7)]">
              {slide.title}
            </h1>
            <p className="mt-4 text-lg md:text-xl max-w-2xl mx-auto drop-shadow-[0_2px_2px_rgba(245,158,11,0.7)]">
              {slide.description}
            </p>
            <a
              href={slide.link}
              className="mt-6 inline-block px-8 py-3 bg-gradient-to-r from-yellow-500 to-yellow-700 text-white text-lg font-semibold rounded-lg shadow-md hover:scale-105 transform transition-all"
            >
              Shop Now
            </a>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default HomeCarousel;
