import React, { useRef } from "react";
import { motion, useScroll } from "framer-motion";
import mosaic_pillow from "../assets/mosaic_pillow.jpg";
import crochet_bag from "../assets/crochet_bag.png";
import crochet_best_seller from "../assets/crochet_best_seller.jpg";

const slides = [
  {
    id: 1,
    title: "Latest Collection",
    description: "Explore our handpicked collection for the season.",
    image: mosaic_pillow,
    link: "/latest-collection",
  },
  {
    id: 2,
    title: "New Arrivals",
    description: "Be the first to grab our latest arrivals.",
    image: crochet_bag,
    link: "/new-arrivals",
  },
  {
    id: 3,
    title: "Best Sellers",
    description: "Shop the most-loved products by our customers.",
    image: crochet_best_seller,
    link: "/best-sellers",
  },
];

const HomeCarousel = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ target: ref });

  return (
    <div
      ref={ref}
      className="relative w-screen h-screen overflow-y-scroll snap-y snap-mandatory"
    >
      {slides.map((slide) => (
        <motion.div
          key={slide.id}
          className="relative w-full h-screen flex items-center justify-center snap-center"
        >
          {/* Background Image with Overlay */}
          <div className="absolute inset-0">
            <img
              src={slide.image} // Access the correct image for each slide
              alt={slide.title}
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-yellow-500 opacity-50"></div> {/* Adjusted opacity */}
          </div>

          {/* Overlay Content */}
          <div className="relative z-10 text-center text-white px-6">
            <h1 className="text-5xl font-extrabold tracking-tight drop-shadow-[0_4px_4px_rgba(245,158,11,0.5)]">
              {slide.title}
            </h1>
            <p className="mt-4 text-lg md:text-xl max-w-2xl mx-auto drop-shadow-[0_2px_2px_rgba(245,158,11,0.5)]">
              {slide.description}
            </p>
            <a
              href={slide.link}
              className="mt-6 inline-block px-8 py-3 bg-gradient-to-r from-yellow-400 to-yellow-600 text-white text-lg font-semibold rounded-full shadow-lg hover:scale-110 hover:shadow-xl transform transition-all flex items-center justify-center gap-2"
            >
              Shop Now
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="w-5 h-5"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                strokeWidth={2}
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M9 5l7 7-7 7"
                />
              </svg>
            </a>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default HomeCarousel;
