import React, { useRef } from "react";
import { motion, useScroll, useTransform } from "framer-motion";

const images = [
  { id: 1, src: "https://flowbite.com/docs/images/carousel/carousel-1.svg" },
  { id: 2, src: "https://flowbite.com/docs/images/carousel/carousel-2.svg" },
  { id: 3, src: "https://flowbite.com/docs/images/carousel/carousel-3.svg" },
];

function useParallax(scrollYProgress: any, distance: number) {
  return useTransform(scrollYProgress, [0, 1], [-distance, distance]);
}

const ParallaxImage = ({ image, scrollYProgress }: { image: any; scrollYProgress: any }) => {
  const y = useParallax(scrollYProgress, 100); // Use the custom hook correctly here

  return (
    <motion.div
      className="relative w-full h-80 flex-shrink-0 snap-center"
      style={{ scrollSnapAlign: "center" }}
    >
      <img
        src={image.src}
        alt={`Image #${image.id}`}
        className="w-full h-full object-cover"
      />
      <motion.h2
        className="absolute bottom-5 left-5 text-white text-2xl"
        style={{ y }}
      >
        Image #{image.id}
      </motion.h2>
    </motion.div>
  );
};

const HomeCarousel = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ target: ref });

  return (
    <div className="relative w-screen h-screen overflow-hidden">
      {/* Carousel Container */}
      <motion.div
        className="flex overflow-x-scroll snap-x snap-mandatory space-x-5"
        drag="x"
        dragConstraints={{ left: 0, right: 0 }}
        style={{ cursor: "grab" }}
      >
        {images.map((image) => (
          <ParallaxImage key={image.id} image={image} scrollYProgress={scrollYProgress} />
        ))}
      </motion.div>
    </div>
  );
};

export default HomeCarousel;
