import React, { useState, useRef, useEffect } from "react";
import HomeCarousel from "../components/HomeCarousel.tsx";
import "../index.css";
import LatestCollections from "../components/LatestCollections.tsx";
import BestSellers from "../components/BestSellers.tsx";

const Home = () => {
  const [showContent, setShowContent] = useState(false);
  const carouselRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setShowContent(true); // Show content when HomeCarousel is fully scrolled out of view
        } else {
          setShowContent(false); // Hide content when scrolling back
        }
      },
      { threshold: 0.1 } // Trigger when 10% of HomeCarousel is out of view
    );

    if (carouselRef.current) {
      observer.observe(carouselRef.current);
    }

    return () => {
      if (carouselRef.current) {
        observer.unobserve(carouselRef.current);
      }
    };
  }, []);

  return (
    <div className="flex flex-col pt-16 w-full">
      <div ref={carouselRef}>
        <HomeCarousel />
      </div>
      {showContent && (
        <>
        <div className = "flex flex-col space-y-74">
          <LatestCollections />
          <BestSellers />
        </div>

        </>
      )}
    </div>
  );
};

export default Home;
