import React from 'react';
import HomeTitleHeader from './HomeTitleHeader';
import ItemCard from './ItemCard.tsx';

const BestSellers = () => {
  return (
    <div className="flex flex-col items-center bg-gray-50 py-10 px-4">
      {/* Section Header */}
      <div className="text-center">
        <HomeTitleHeader text1="BEST" text2="SELLERS" />
        <p className="text-gray-600 text-sm mt-2">
          Explore our most popular items loved by customers worldwide!
        </p>
      </div>

      {/* Grid of Items */}
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6 py-8 w-full max-w-screen-xl">
        {Array.from({ length: 8 }).map((_, index) => (
          <ItemCard key={index} />
        ))}
      </div>

      {/* CTA */}
      <button className="mt-6 px-6 py-3 bg-yellow-500 text-white rounded-full text-sm font-medium hover:bg-yellow-600 transition-colors duration-300">
        View All Best Sellers
      </button>
    </div>
  );
};

export default BestSellers;
