import React, { useRef } from "react";
import { motion, useScroll } from "framer-motion";

const slides = [
  {
    id: 1,
    title: "Latest Collection",
    description: "Discover the newest styles for the season.",
    image: "https://via.placeholder.com/1200x800?text=Latest+Collection",
    link: "/latest-collection",
  },
  {
    id: 2,
    title: "New Arrivals",
    description: "Fresh arrivals, just for you.",
    image: "https://via.placeholder.com/1200x800?text=New+Arrivals",
    link: "/new-arrivals",
  },
  {
    id: 3,
    title: "Best Sellers",
    description: "Our customers' all-time favorites.",
    image: "https://via.placeholder.com/1200x800?text=Best+Sellers",
    link: "/best-sellers",
  },
];

const HomeCarousel = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ target: ref });

  return (
    <div
      ref={ref}
      className="relative w-screen h-screen overflow-y-scroll snap-y snap-mandatory"
    >
      {slides.map((slide) => (
        <motion.div
          key={slide.id}
          className="relative w-full h-screen flex items-center justify-center snap-center"
        >
          {/* Background Image */}
          <img
            src={slide.image}
            alt={slide.title}
            className="absolute w-full h-full object-cover"
          />
          {/* Overlay Content */}
          <div className="relative z-10 p-8 bg-black bg-opacity-50 text-white rounded-xl text-center">
            <h1 className="text-4xl font-bold">{slide.title}</h1>
            <p className="mt-4 text-lg">{slide.description}</p>
            <a
              href={slide.link}
              className="mt-6 inline-block px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
            >
              Shop Now
            </a>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default HomeCarousel;
